import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Payment = () => {
    const [amount, setAmount] = useState("");
    const navigate = useNavigate();

    const handlePayment = async () => {
        try {
            const response = await fetch("http://localhost:5000/api/payment/create-order", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ amount: amount * 100 }), // Convert to smallest currency unit (paisa)
            });

            if (!response.ok) {
                throw new Error("Failed to create payment order");
            }

            const orderData = await response.json();

            const options = {
                key: process.env.REACT_APP_RAZORPAY_KEY_ID, // Enter the Key ID generated from the Razorpay Dashboard
                amount: orderData.amount,
                currency: orderData.currency,
                name: "Your Company Name",
                description: "Test Transaction",
                order_id: orderData.id,
                handler: function (response) {
                    alert(`Payment successful: ${response.razorpay_payment_id}`);
                    console.log(response);
                    
                    // Navigate to profile page after successful payment
                    navigate("/profile");
                },
                prefill: {
                    name: "Bhavna Bairagi",
                    email: "bhavnabairagi12@gmail.com",
                    contact: "8839022132",
                },
                theme: {
                    color: "#3399cc",
                },
                // This ensures the handler function is called even if the modal is closed
                modal: {
                    ondismiss: function() {
                        console.log("Payment modal closed without completion");
                    }
                }
            };

            const razorpay = new window.Razorpay(options);
            razorpay.open();
        } catch (error) {
            console.error("Payment error:", error);
            alert("Something went wrong with the payment. Please try again.");
        }
    };

    return (
        <div className="payment-container p-6 max-w-md mx-auto bg-white rounded-lg shadow-md">
            <h2 className="text-2xl font-bold mb-6 text-center">Payment</h2>
            <div className="mb-4">
                <label htmlFor="amount" className="block text-gray-700 mb-2">Amount (â‚¹)</label>
                <input
                    id="amount"
                    type="number"
                    placeholder="Enter Amount"
                    value={amount}
                    onChange={(e) => setAmount(e.target.value)}
                    className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    min="1"
                />
            </div>
            <button 
                onClick={handlePayment}
                disabled={!amount}
                className="w-full bg-blue-600 hover:bg-blue-700 text-white font-medium py-2 px-4 rounded-md transition duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
            >
                Pay Now
            </button>
        </div>
    );
};

export default Payment;
