import React, { useState } from 'react';
import { FaSearch } from 'react-icons/fa';
import axios from 'axios';
import '../styles/Search.css';

const Search = () => {
  const [searchParams, setSearchParams] = useState({
    type: 'bride',
    ageFrom: '18',
    ageTo: '40',
    heightFrom: '',
    heightTo: '',
    education: '',
    caste: '',
    religion: '',
    native: ''
  });
  
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [pagination, setPagination] = useState({
    total: 0,
    page: 1,
    pages: 1
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setSearchParams({
      ...searchParams,
      [name]: value
    });
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        setError('You must be logged in to search profiles');
        setLoading(false);
        return;
      }
      
      const response = await axios.post(
        'http://localhost:5000/api/users/filter',
        searchParams,
        {
          headers: { Authorization: `Bearer ${token}` },
          params: { page: 1, limit: 10 }
        }
      );
      
      setResults(response.data.profiles);
      setPagination(response.data.pagination);
      setLoading(false);
    } catch (err) {
      console.error('Search error:', err);
      setError('Failed to fetch search results. Please try again.');
      setLoading(false);
    }
  };

  const loadPage = async (page) => {
    setLoading(true);
    
    try {
      const token = localStorage.getItem('token');
      
      const response = await axios.post(
        'http://localhost:5000/api/user/filter',
        searchParams,
        {
          headers: { Authorization: `Bearer ${token}` },
          params: { page, limit: 10 }
        }
      );
      
      setResults(response.data.profiles);
      setPagination(response.data.pagination);
      setLoading(false);
    } catch (err) {
      setError('Failed to load page. Please try again.');
      setLoading(false);
    }
  };

  // Height conversion for display (cm to feet and inches)
  const formatHeight = (heightInCm) => {
    if (!heightInCm) return 'Not specified';
    
    const totalInches = heightInCm / 2.54;
    const feet = Math.floor(totalInches / 12);
    const inches = Math.round(totalInches % 12);
    
    return `${feet}'${inches}" (${heightInCm} cm)`;
  };

  return (
    <div className="search-container">
      <div className="search-header">
        <h2>Search Profiles</h2>
        <p>Find your perfect match by using our advanced search filters</p>
      </div>
      
      <div className="search-form-container">
        <form onSubmit={handleSearch} className="search-form">
          <div className="search-fields">
            <div className="form-group">
              <label>Looking for</label>
              <select 
                name="type" 
                value={searchParams.type}
                onChange={handleChange}
              >
                <option value="bride">Bride</option>
                <option value="groom">Groom</option>
                <option value="divorceBride">Divorce Bride</option>
                <option value="divorceGroom">Divorce Groom</option>
              </select>
            </div>
            
            <div className="form-group">
              <label>Age Range</label>
              <div className="range-inputs">
                <select 
                  name="ageFrom" 
                  value={searchParams.ageFrom}
                  onChange={handleChange}
                >
                  {Array.from({ length: 43 }, (_, i) => i + 18).map(age => (
                    <option key={`from-${age}`} value={age}>{age}</option>
                  ))}
                </select>
                <span>to</span>
                <select 
                  name="ageTo" 
                  value={searchParams.ageTo}
                  onChange={handleChange}
                >
                  {Array.from({ length: 43 }, (_, i) => i + 18).map(age => (
                    <option key={`to-${age}`} value={age}>{age}</option>
                  ))}
                </select>
              </div>
            </div>
            
              <div className="form-group">
              <label>Height Range (feet)</label>
              <div className="range-inputs">
              <input 
                type="number" 
                name="heightFrom" 
                placeholder="From" 
                value={searchParams.heightFrom}
                onChange={handleChange}
                min="3.0"
                max="7.0"
                step="0.1"
             />
             <span>to</span>
             <input 
                type="number" 
                name="heightTo" 
                placeholder="To" 
                value={searchParams.heightTo}
                onChange={handleChange}
                min="3.0"
                max="7.0"
                step="0.1"
               />
             </div>
            </div>
            
            <div className="form-group">
              <label>Education</label>
              <select 
                name="education" 
                value={searchParams.education}
                onChange={handleChange}
              >
                <option value="">Any</option>
                <option value="High School">High School</option>
                <option value="Bachelor's">Bachelor's</option>
                <option value="Master's">Master's</option>
                <option value="Doctorate">Doctorate</option>
                <option value="Other">Other</option>
              </select>
            </div>
            
            <div className="form-group">
              <label>Religion</label>
              <select 
                name="religion" 
                value={searchParams.religion}
                onChange={handleChange}
              >
                <option value="Any">Any</option>
                <option value="Hindu">Hindu</option>
                <option value="Muslim">Muslim</option>
                <option value="Christian">Christian</option>
                <option value="Sikh">Sikh</option>
                <option value="Buddhist">Buddhist</option>
                <option value="Jain">Jain</option>
                <option value="Other">Other</option>
              </select>
            </div>
            
            <div className="form-group">
              <label>Caste</label>
              <input 
                type="text" 
                name="caste" 
                placeholder="Any caste" 
                value={searchParams.caste}
                onChange={handleChange}
              />
            </div>
            
            <div className="form-group">
              <label>Location</label>
              <input 
                type="text" 
                name="location" 
                placeholder="Any location" 
                value={searchParams.location}
                onChange={handleChange}
              />
            </div>
          </div>
          
          <div className="search-button-container">
            <button type="submit" className="search-button" disabled={loading}>
              <FaSearch /> {loading ? 'Searching...' : 'Search'}
            </button>
          </div>
        </form>
      </div>
      
      {error && <div className="search-error">{error}</div>}
      
      <div className="search-results">
        <h3>Search Results {results.length > 0 && `(${pagination.total})`}</h3>
        
        {loading ? (
          <div className="loading-spinner">Loading profiles...</div>
        ) : results.length > 0 ? (
          <>
            <div className="results-grid">
              {results.map(profile => (
                <div key={profile._id} className="profile-card">
                  <div className="profile-image">
                    {profile.profilePicture ? (
                      <img src={profile.profilePicture} alt={profile.fullname} />
                    ) : (
                      <div className="default-avatar">
                        {profile.fullname.charAt(0).toUpperCase()}
                      </div>
                    )}
                    {profile.isPremium && <span className="premium-badge">Premium</span>}
                  </div>
                  
                  <div className="profile-info">
                    <h4>{profile.fullname}</h4>
                    <p className="profile-basic">
                      {profile.age} years • {formatHeight(profile.height)}
                    </p>
                    <p className="profile-details">
                      {profile.religion}{profile.caste ? `, ${profile.caste}` : ''}
                    </p>
                    <p className="profile-location">{profile.location || profile.city}</p>
                    <p className="profile-education">
                      {profile.education}{profile.occupation ? ` • ${profile.occupation}` : ''}
                    </p>
                    
                    <button className="view-profile-btn">View Profile</button>
                  </div>
                </div>
              ))}
            </div>
            
            {pagination.pages > 1 && (
              <div className="pagination">
                <button 
                  disabled={pagination.page === 1} 
                  onClick={() => loadPage(pagination.page - 1)}
                >
                  Previous
                </button>
                
                <span>Page {pagination.page} of {pagination.pages}</span>
                
                <button 
                  disabled={pagination.page === pagination.pages} 
                  onClick={() => loadPage(pagination.page + 1)}
                >
                  Next
                </button>
              </div>
            )}
          </>
        ) : (
          <div className="no-results">
            <p>No profiles match your search criteria.</p>
            <p>Try broadening your search parameters.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Search;
