// VerifyEmail.js (New component for email verification)
import React, { useEffect, useState } from "react";
import { useParams, useNavigate, Link } from "react-router-dom";
import axios from "axios";
import "../styles/VerifyEmail.css";

function VerifyEmail() {
  const { token } = useParams();
  const [status, setStatus] = useState("verifying");
  const [email, setEmail] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const verifyEmail = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/auth/verify-email/${token}`);
        setStatus("success");
        setEmail(response.data.email);
        
        // Redirect to login page after 3 seconds
        setTimeout(() => {
          navigate(`/login?verified=true&email=${response.data.email}`);
        }, 3000);
      } catch (error) {
        setStatus("failed");
      }
    };

    if (token) {
      verifyEmail();
    }
  }, [token, navigate]);

  return (
    <div className="verify-container">
      <div className="verify-card">
        {status === "verifying" && (
          <>
            <div className="loading-spinner"></div>
            <h2>Verifying your email...</h2>
            <p>Please wait while we confirm your email address.</p>
          </>
        )}

        {status === "success" && (
          <>
            <div className="success-icon">✓</div>
            <h2>Email Verified Successfully!</h2>
            <p>Your email ({email}) has been verified.</p>
            <p>Redirecting you to login page...</p>
            <Link to="/login" className="login-link">Click here if you are not redirected automatically</Link>
          </>
        )}

        {status === "failed" && (
          <>
            <div className="error-icon">✗</div>
            <h2>Verification Failed</h2>
            <p>The verification link is invalid or has expired.</p>
            <Link to="/login" className="login-link">Return to Login</Link>
          </>
        )}
      </div>
    </div>
  );
}

export default VerifyEmail;
