import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import "../styles/Login.css";

function Login() {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    userType: "" // New field for user type selection
  });
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  
  const navigate = useNavigate();
  
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    
    // Validate user type selection
    if (!formData.userType) {
      setError("Please select your user type");
      setLoading(false);
      return;
    }
    
    try {
      // The endpoint will change based on user type
      const endpoint = formData.userType === "admin" 
        ? "http://localhost:5000/api/admin/login" 
        : "http://localhost:5000/api/user/login";
      
      const response = await axios.post(endpoint, {
        email: formData.email,
        password: formData.password
      });
      
      localStorage.setItem("token", response.data.token);
      localStorage.setItem("userType", formData.userType);
      
      // Redirect to profile page
      navigate("/profile");
    } catch (err) {
      console.error("Login error:", err);
      setError(err.response?.data?.message || "Login failed. Please check your credentials.");
    } finally {
      setLoading(false);
    }
  };
  
  return (
    <div className="login-container">
      <div className="login-card">
        <h2>Login</h2>
        
        {error && <div className="error-message">{error}</div>}
        
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label>User Type</label>
            <div className="user-type-selection">
              <label className={`user-type-option ${formData.userType === 'user' ? 'selected' : ''}`}>
                <input
                  type="radio"
                  name="userType"
                  value="user"
                  checked={formData.userType === "user"}
                  onChange={handleChange}
                />
                <div className="option-label">
                  <span className="option-icon">👤</span>
                  <span className="option-text">User</span>
                </div>
              </label>
            </div>
          </div>
          
          <button 
            type="submit" 
            className="login-btn" 
            disabled={loading}
          >
            {loading ? "Logging in..." : "Login"}
          </button>
        </form>
        
        <div className="auth-footer">
          <p>
            Don't have an account? <Link to="/register">Register</Link>
          </p>
        </div>
      </div>
    </div>
  );
}

export default Login;
