import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/SearchFilter.css';
import { ProfileCard } from './ProfileCard';

const SearchFilter = () => {
  // State for filter criteria
  const [filters, setFilters] = useState({
    looking: '',
    gender: '',
    minAge: '',
    maxAge: '',
    religion: '',
    caste: '',
    education: '',
    familyType: '',
    familyStatus: '',
    location: '',
  });

  // State for filtered results
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // State for available dropdown options (to be fetched from backend)
  const [religions, setReligions] = useState([]);
  const [castes, setCastes] = useState([]);
  const [educations, setEducations] = useState([]);
  const [locations, setLocations] = useState([]);

  // Load dropdown options on component mount
  useEffect(() => {
    const fetchDropdownOptions = async () => {
      try {
        const [religionsRes, educationsRes, locationsRes] = await Promise.all([
          axios.get('/api/filters/religions'),
          axios.get('/api/filters/educations'),
          axios.get('/api/filters/locations')
        ]);
        
        setReligions(religionsRes.data);
        setEducations(educationsRes.data);
        setLocations(locationsRes.data);
      } catch (err) {
        console.error('Error fetching dropdown options:', err);
        setError('Failed to load filter options');
      }
    };

    fetchDropdownOptions();
  }, []);

  // Handle changes to caste dropdown based on selected religion
  useEffect(() => {
    if (filters.religion) {
      axios.get(`/api/filters/castes/${filters.religion}`)
        .then(res => setCastes(res.data))
        .catch(err => console.error('Error fetching castes:', err));
    } else {
      setCastes([]);
    }
  }, [filters.religion]);

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  // Handle search submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      // Remove empty filters
      const activeFilters = Object.entries(filters)
        .filter(([_, value]) => value !== '')
        .reduce((obj, [key, value]) => ({ ...obj, [key]: value }), {});
      
      const response = await axios.post('/api/search', activeFilters);
      setResults(response.data);
    } catch (err) {
      console.error('Search error:', err);
      setError('Failed to perform search. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Handle filter reset
  const handleReset = () => {
    setFilters({
      looking: '',
      gender: '',
      minAge: '',
      maxAge: '',
      religion: '',
      caste: '',
      education: '',
      familyType: '',
      familyStatus: '',
      location: '',
    });
    setResults([]);
  };

  return (
    <div className="search-filter-container">
      <h2>Find Your Perfect Match</h2>
      
      <form onSubmit={handleSubmit} className="filter-form">
        <div className="filter-row">
          <div className="filter-group">
            <label>Looking for</label>
            <select 
              name="looking" 
              value={filters.looking} 
              onChange={handleChange}
            >
              <option value="">Select</option>
              <option value="bride">Bride</option>
              <option value="groom">Groom</option>
              <option value="divorce bride">Divorce Bride</option>
              <option value="divorce groom">Divorce Groom</option>
            </select>
          </div>

          <div className="filter-group">
            <label>Gender</label>
            <select 
              name="gender" 
              value={filters.gender} 
              onChange={handleChange}
            >
              <option value="">Select</option>
              <option value="male">Male</option>
              <option value="female">Female</option>
              <option value="other">Other</option>
            </select>
          </div>
        </div>

        <div className="filter-row">
          <div className="filter-group">
            <label>Age Range</label>
            <div className="age-range">
              <input 
                type="number" 
                name="minAge" 
                placeholder="Min" 
                value={filters.minAge} 
                onChange={handleChange}
                min="18"
                max="100"
              />
              <span>to</span>
              <input 
                type="number" 
                name="maxAge" 
                placeholder="Max" 
                value={filters.maxAge} 
                onChange={handleChange}
                min="18" 
                max="100"
              />
            </div>
          </div>

          <div className="filter-group">
            <label>Religion</label>
            <select 
              name="religion" 
              value={filters.religion} 
              onChange={handleChange}
            >
              <option value="">Select</option>
              {religions.map((religion, index) => (
                <option key={index} value={religion}>{religion}</option>
              ))}
            </select>
          </div>
        </div>

        <div className="filter-row">
          <div className="filter-group">
            <label>Caste</label>
            <select 
              name="caste" 
              value={filters.caste} 
              onChange={handleChange}
              disabled={!filters.religion}
            >
              <option value="">Select</option>
              {castes.map((caste, index) => (
                <option key={index} value={caste}>{caste}</option>
              ))}
            </select>
          </div>

          <div className="filter-group">
            <label>Education</label>
            <select 
              name="education" 
              value={filters.education} 
              onChange={handleChange}
            >
              <option value="">Select</option>
              {educations.map((education, index) => (
                <option key={index} value={education}>{education}</option>
              ))}
            </select>
          </div>
        </div>

        <div className="filter-row">
          <div className="filter-group">
            <label>Family Type</label>
            <select 
              name="familyType" 
              value={filters.familyType} 
              onChange={handleChange}
            >
              <option value="">Select</option>
              <option value="Joint">Joint</option>
              <option value="Nuclear">Nuclear</option>
            </select>
          </div>

          <div className="filter-group">
            <label>Family Status</label>
            <select 
              name="familyStatus" 
              value={filters.familyStatus} 
              onChange={handleChange}
            >
              <option value="">Select</option>
              <option value="Middle Class">Middle Class</option>
              <option value="Upper Middle Class">Upper Middle Class</option>
              <option value="Rich">Rich</option>
              <option value="Affluent">Affluent</option>
            </select>
          </div>
        </div>

        <div className="filter-row">
          <div className="filter-group">
            <label>Location</label>
            <select 
              name="location" 
              value={filters.location} 
              onChange={handleChange}
            >
              <option value="">Select</option>
              {locations.map((location, index) => (
                <option key={index} value={location}>{location}</option>
              ))}
            </select>
          </div>
        </div>

        <div className="filter-buttons">
          <button type="submit" className="search-btn" disabled={loading}>
            {loading ? 'Searching...' : 'Search Profiles'}
          </button>
          <button type="button" className="reset-btn" onClick={handleReset}>
            Reset Filters
          </button>
        </div>
      </form>

      {error && <div className="error-message">{error}</div>}

      <div className="search-results">
        <h3>{results.length > 0 ? `Found ${results.length} Matches` : 'No results yet'}</h3>
        <div className="results-grid">
          {results.map(profile => (
            <ProfileCard key={profile._id} profile={profile} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default SearchFilter;
