// src/components/Dashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';
import { Line, Bar, Pie } from 'react-chartjs-2';

// Register Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalAppointments: 0,
    totalCustomers: 0,
    totalRevenue: 0,
    recentAppointments: [],
    serviceStats: [],
    monthlyRevenue: [],
    customerGrowth: []
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('/api/dashboard', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setStats(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to load dashboard data. Please try again later.');
        setLoading(false);
        console.error('Dashboard data fetch error:', err);
      }
    };

    fetchDashboardData();
  }, []);

  // Revenue chart data
  const revenueData = {
    labels: stats.monthlyRevenue.map(item => item.month),
    datasets: [
      {
        label: 'Monthly Revenue',
        data: stats.monthlyRevenue.map(item => item.amount),
        fill: false,
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        borderColor: 'rgba(75, 192, 192, 1)',
        tension: 0.1
      }
    ]
  };

  // Service popularity chart data
  const serviceData = {
    labels: stats.serviceStats.map(service => service.name),
    datasets: [
      {
        label: 'Service Bookings',
        data: stats.serviceStats.map(service => service.count),
        backgroundColor: [
          'rgba(255, 99, 132, 0.6)',
          'rgba(54, 162, 235, 0.6)',
          'rgba(255, 206, 86, 0.6)',
          'rgba(75, 192, 192, 0.6)',
          'rgba(153, 102, 255, 0.6)',
        ],
        borderWidth: 1,
      },
    ],
  };

  // Customer growth chart
  const customerData = {
    labels: stats.customerGrowth.map(item => item.month),
    datasets: [
      {
        label: 'New Customers',
        data: stats.customerGrowth.map(item => item.count),
        backgroundColor: 'rgba(153, 102, 255, 0.6)',
      },
    ],
  };

  if (loading) return <div className="loading">Loading dashboard data...</div>;
  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="dashboard-container">
      <h1>OneStopSalon Dashboard</h1>
      
      {/* Summary Cards */}
      <div className="summary-cards">
        <div className="card">
          <h3>Total Appointments</h3>
          <p className="card-value">{stats.totalAppointments}</p>
        </div>
        <div className="card">
          <h3>Total Customers</h3>
          <p className="card-value">{stats.totalCustomers}</p>
        </div>
        <div className="card">
          <h3>Total Revenue</h3>
          <p className="card-value">${stats.totalRevenue.toFixed(2)}</p>
        </div>
      </div>

      {/* Charts Section */}
      <div className="charts-container">
        <div className="chart-card">
          <h3>Monthly Revenue</h3>
          <Line data={revenueData} />
        </div>
        
        <div className="chart-card">
          <h3>Service Popularity</h3>
          <Pie data={serviceData} />
        </div>
        
        <div className="chart-card">
          <h3>Customer Growth</h3>
          <Bar data={customerData} />
        </div>
      </div>

      {/* Recent Appointments Table */}
      <div className="recent-appointments">
        <h3>Recent Appointments</h3>
        <table>
          <thead>
            <tr>
              <th>Customer</th>
              <th>Service</th>
              <th>Date</th>
              <th>Time</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {stats.recentAppointments.map(appointment => (
              <tr key={appointment._id}>
                <td>{appointment.customerName}</td>
                <td>{appointment.serviceName}</td>
                <td>{new Date(appointment.date).toLocaleDateString()}</td>
                <td>{appointment.time}</td>
                <td>
                  <span className={`status-badge ${appointment.status.toLowerCase()}`}>
                    {appointment.status}
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Dashboard;
