import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const ProfileNavbar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  
  const isActive = (path) => {
    return location.pathname === path;
  };
  
  return (
    <nav className="bg-gray-800 text-white p-4 mb-6">
      <div className="container mx-auto">
        <div className="flex flex-col md:flex-row justify-between items-center">
          <h1 className="text-xl font-bold mb-4 md:mb-0">User Management</h1>
          
          <div className="flex flex-wrap gap-4">
            <button 
              onClick={() => navigate('/profile')}
              className={`px-4 py-2 rounded ${isActive('/profile') 
                ? 'bg-blue-600' 
                : 'bg-gray-700 hover:bg-gray-600'}`}
            >
              My Profile
            </button>
            
            <div className="relative group">
              <button 
                onClick={() => navigate('/users')}
                className={`px-4 py-2 rounded ${isActive('/users') || isActive('/users/active') || isActive('/users/deactive') || isActive('/users/pending')
                  ? 'bg-blue-600' 
                  : 'bg-gray-700 hover:bg-gray-600'}`}
              >
                User List
              </button>
              
              <div className="absolute hidden group-hover:block bg-gray-700 mt-2 rounded shadow-lg z-10">
                <button 
                  onClick={() => navigate('/users/active')}
                  className="block px-4 py-2 w-full text-left hover:bg-gray-600"
                >
                  Active Users
                </button>
                <button 
                  onClick={() => navigate('/users/deactive')}
                  className="block px-4 py-2 w-full text-left hover:bg-gray-600"
                >
                  Deactivated Users
                </button>
                <button 
                  onClick={() => navigate('/users/pending')}
                  className="block px-4 py-2 w-full text-left hover:bg-gray-600"
                >
                  Pending Users
                </button>
              </div>
            </div>
            
            <button 
              onClick={() => navigate('/profile/edit')}
              className={`px-4 py-2 rounded ${isActive('/profile/edit') 
                ? 'bg-blue-600' 
                : 'bg-gray-700 hover:bg-gray-600'}`}
            >
              Edit Profile
            </button>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default ProfileNavbar;
