import React, { useState, useEffect } from 'react';
import '../styles/GalleryPage.css';

const GalleryPage = () => {
  // Sample gallery data - in a real application, this would come from an API
  const [galleries, setGalleries] = useState([
    {
      id: 1,
      title: 'Raj & Priya Wedding',
      description: 'Traditional wedding ceremony in Mumbai',
      category: 'wedding',
      imageUrl: '/api/placeholder/400/500',
      featured: true
    },
    {
      id: 2,
      title: 'Arjun & Meera Engagement',
      description: 'A beautiful engagement ceremony in Delhi',
      category: 'engagement',
      imageUrl: '/api/placeholder/400/500',
      featured: false
    },
    {
      id: 3,
      title: 'Vikram & Anjali Pre-wedding',
      description: 'Pre-wedding photoshoot in Udaipur',
      category: 'pre-wedding',
      imageUrl: '/api/placeholder/400/500',
      featured: true
    },
    {
      id: 4,
      title: 'Karan & Nisha Reception',
      description: 'Grand reception at The Leela Palace',
      category: 'reception',
      imageUrl: '/api/placeholder/400/500',
      featured: false
    },
    {
      id: 5,
      title: 'Aditya & Sneha Wedding',
      description: 'South Indian wedding ceremony in Chennai',
      category: 'wedding',
      imageUrl: '/api/placeholder/400/500',
      featured: false
    },
    {
      id: 6,
      title: 'Rohit & Aisha Engagement',
      description: 'Intimate engagement ceremony in Bangalore',
      category: 'engagement',
      imageUrl: '/api/placeholder/400/500',
      featured: true
    },
    {
      id: 7,
      title: 'Nikhil & Riya Pre-wedding',
      description: 'Romantic pre-wedding shoot in Goa',
      category: 'pre-wedding',
      imageUrl: '/api/placeholder/400/500',
      featured: false
    },
    {
      id: 8,
      title: 'Varun & Pooja Reception',
      description: 'Elegant reception event in Jaipur',
      category: 'reception',
      imageUrl: '/api/placeholder/400/500',
      featured: true
    },
    {
      id: 9,
      title: 'Siddharth & Neha Wedding',
      description: 'Punjabi wedding celebration in Chandigarh',
      category: 'wedding',
      imageUrl: '/api/placeholder/400/500',
      featured: false
    }
  ]);

  const [loading, setLoading] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [currentImage, setCurrentImage] = useState(null);

  const categories = ['all', 'wedding', 'engagement', 'pre-wedding', 'reception'];

  const filteredGalleries = selectedCategory === 'all' 
    ? galleries 
    : galleries.filter(gallery => gallery.category === selectedCategory);

  const openLightbox = (image) => {
    setCurrentImage(image);
    setLightboxOpen(true);
    document.body.style.overflow = 'hidden';
  };

  const closeLightbox = () => {
    setLightboxOpen(false);
    document.body.style.overflow = 'auto';
  };

  const nextImage = () => {
    const currentIndex = filteredGalleries.findIndex(gallery => gallery.id === currentImage.id);
    const nextIndex = (currentIndex + 1) % filteredGalleries.length;
    setCurrentImage(filteredGalleries[nextIndex]);
  };

  const prevImage = () => {
    const currentIndex = filteredGalleries.findIndex(gallery => gallery.id === currentImage.id);
    const prevIndex = (currentIndex - 1 + filteredGalleries.length) % filteredGalleries.length;
    setCurrentImage(filteredGalleries[prevIndex]);
  };

  if (loading) {
    return <div className="gallery-loading">Loading beautiful moments...</div>;
  }

  return (
    <div className="gallery-page">
      <div className="gallery-header">
        <h1>Our Beautiful Matches</h1>
        <p>Moments that celebrate love and togetherness</p>
      </div>

      <div className="gallery-categories">
        {categories.map(category => (
          <button 
            key={category} 
            className={`category-btn ${selectedCategory === category ? 'active' : ''}`}
            onClick={() => setSelectedCategory(category)}
          >
            {category.charAt(0).toUpperCase() + category.slice(1)}
          </button>
        ))}
      </div>

      <div className="gallery-grid">
        {filteredGalleries.length > 0 ? (
          filteredGalleries.map(gallery => (
            <div 
              className="gallery-item" 
              key={gallery.id}
              onClick={() => openLightbox(gallery)}
            >
              <div className="gallery-image-container">
                <img 
                  src={gallery.imageUrl} 
                  alt={gallery.title} 
                  className="gallery-image"
                />
                <div className="gallery-overlay">
                  <div className="gallery-details">
                    <h3>{gallery.title}</h3>
                    <p>{gallery.description}</p>
                  </div>
                </div>
              </div>
            </div>
          ))
        ) : (
          <div className="no-images">No images found in this category</div>
        )}
      </div>

      {lightboxOpen && currentImage && (
        <div className="lightbox" onClick={closeLightbox}>
          <div className="lightbox-content" onClick={e => e.stopPropagation()}>
            <button className="lightbox-close" onClick={closeLightbox}>×</button>
            <button className="lightbox-prev" onClick={prevImage}>‹</button>
            <div className="lightbox-image-container">
              <img 
                src={currentImage.imageUrl} 
                alt={currentImage.title} 
                className="lightbox-image"
              />
              <div className="lightbox-details">
                <h3>{currentImage.title}</h3>
                <p>{currentImage.description}</p>
                <span className="lightbox-category">{currentImage.category}</span>
              </div>
            </div>
            <button className="lightbox-next" onClick={nextImage}>›</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default GalleryPage;
