import React, { useState, useEffect } from "react";
import axios from "axios";
import '../styles/PayPalButton.css'

const PayPalButton = ({ recipeId, recipeTitle, price }) => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [redirectUrl, setRedirectUrl] = useState(null);

  // Format the price to match backend expectations
  const formattedPrice = parseFloat(price).toFixed(2);

  const handlePayment = async () => {
    try {
      // Get the token from your backend
      const response = await axios.post('http://localhost:5000/api/paypal-routes/create-order/12345');
      const { token } = response.data;
      
      // Redirect the browser to PayPal (not using AJAX)
      window.location.href = `https://www.sandbox.paypal.com/cgi-bin/webscr?cmd=_express-checkout&token=${token}`;
    } catch (error) {
      console.error('Payment initiation error:', error);
    }
  };
  // Listen for success/cancel returns from PayPal
  useEffect(() => {
    const queryParams = new URLSearchParams(window.location.search);
    const paymentId = queryParams.get("paymentId");
    const PayerID = queryParams.get("PayerID");
    
    // Check if this is a return from PayPal
    if (paymentId && PayerID) {
      setLoading(true);
      
      // The backend will handle the rest when hitting these routes
      const successPath = window.location.pathname.includes("success");
      const cancelPath = window.location.pathname.includes("cancel");
      
      if (successPath) {
        // Payment was successful - you can show a success message
        // The backend handles the database operations
        setLoading(false);
      } else if (cancelPath) {
        // Payment was cancelled
        setError("Payment was cancelled.");
        setLoading(false);
      }
    }
  }, []);

  return (
    <div className="paypal-payment-container">
      <div className="payment-header">
        <h2>Complete Your Purchase</h2>
      </div>
      
      <div className="recipe-details">
        <h3>{recipeTitle}</h3>
        <div className="price-tag">${formattedPrice}</div>
      </div>
      
      <div className="payment-actions">
        {loading ? (
          <div className="loading-indicator">
            <div className="loader"></div>
            <span>Processing your payment...</span>
          </div>
        ) : (
          <button 
            className="paypal-button" 
            onClick={initiatePayment}
            disabled={loading}
          >
            <span className="paypal-logo">
              <span className="paypal-logo-p">P</span>
              <span className="paypal-logo-a">a</span>
              <span className="paypal-logo-y">y</span>
              <span className="paypal-logo-p">P</span>
              <span className="paypal-logo-a">a</span>
              <span className="paypal-logo-l">l</span>
            </span>
            Checkout
          </button>
        )}
        
        {error && (
          <div className="error-message">
            {error}
          </div>
        )}
      </div>
      
      <div className="payment-footer">
        <div className="secure-payment">
          <i className="lock-icon">ðŸ”’</i> Secure payment via PayPal
        </div>
        <div className="terms">
          By completing this purchase, you agree to our Terms of Service.
        </div>
      </div>
    </div>
  );
};

export default PayPalButton;
