import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const Payment = () => {
    const [amount, setAmount] = useState("");
    const [userInfo, setUserInfo] = useState(null);
    const navigate = useNavigate();

    // Load user info from localStorage on component mount
    useEffect(() => {
        const userFromStorage = localStorage.getItem("user");
        if (userFromStorage) {
            setUserInfo(JSON.parse(userFromStorage));
        }
    }, []);

    const handlePayment = async () => {
        // Check if user is logged in
        if (!userInfo) {
            alert("Please login first to make a payment");
            navigate("/login");
            return;
        }

        const token = localStorage.getItem("token");
        if (!token) {
            alert("Authentication token missing. Please login again.");
            navigate("/login");
            return;
        }

        try {
            const response = await fetch("http://localhost:5000/api/payment/create-order", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${token}`
                },
                body: JSON.stringify({ amount: amount * 100 }), // Convert to smallest currency unit (paisa)
            });

            const orderData = await response.json();

            const options = {
                key: process.env.REACT_APP_RAZORPAY_KEY_ID,
                amount: orderData.amount,
                currency: orderData.currency,
                name: "Your Company Name",
                description: "Test Transaction",
                order_id: orderData.id,
                handler: async function (response) {
                    alert(`Payment successful: ${response.razorpay_payment_id}`);
                    console.log(response);
                    
                    // Verify payment on your server and create order
                    try {
                        await fetch("http://localhost:5000/api/payment/verify", {
                            method: "POST",
                            headers: {
                                "Content-Type": "application/json",
                                "Authorization": `Bearer ${token}`
                            },
                            body: JSON.stringify({
                                razorpay_payment_id: response.razorpay_payment_id,
                                razorpay_order_id: response.razorpay_order_id,
                                razorpay_signature: response.razorpay_signature
                            })
                        });
                        
                        // Navigate based on user type after successful payment
                        const userType = userInfo.type || userInfo.userType;
                        if (userType === "admin") {
                            navigate("/admin/profile");
                        } else {
                            // First go to create-order page for regular users
                            navigate("/create-order");
                        }
                    } catch (error) {
                        console.error("Payment verification failed:", error);
                        alert("Payment verification failed. Please contact support.");
                    }
                },
                prefill: {
                    name: userInfo?.name || "",
                    email: userInfo?.email || "",
                    contact: userInfo?.phone || ""
                },
                theme: {
                    color: "#3399cc",
                },
            };

            const razorpay = new window.Razorpay(options);
            razorpay.open();
        } catch (error) {
            console.error("Error creating payment order:", error);
            alert("Failed to create payment order. Please try again.");
        }
    };

    return (
        <div className="payment-container">
            <h2>Make Payment</h2>
            
            {!userInfo && (
                <div className="login-message">
                    <p>Please <a href="/login">login</a> to continue with payment</p>
                </div>
            )}
            
            {userInfo && (
                <div className="payment-form">
                    <div className="form-group">
                        <label htmlFor="amount">Amount (â‚¹)</label>
                        <input
                            type="number"
                            id="amount"
                            placeholder="Enter Amount"
                            value={amount}
                            onChange={(e) => setAmount(e.target.value)}
                            min="1"
                            required
                        />
                    </div>
                    
                    <button 
                        className="payment-btn"
                        onClick={handlePayment}
                        disabled={!amount}
                    >
                        Proceed to Pay
                    </button>
                </div>
            )}
        </div>
    );
};

export default Payment;
