import React, { useState } from 'react';
import { User, Users, Edit, PlusCircle, LogOut } from 'lucide-react';

const MatrimonialAdminNavbar = () => {
  const [activeTab, setActiveTab] = useState('profile');
  
  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };
  
  return (
    <div className="w-full bg-gradient-to-r from-pink-700 to-red-700 text-white shadow-md">
      <div className="container mx-auto px-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center py-4">
            <span className="text-xl font-bold">Matrimony Admin</span>
          </div>
          
          <nav className="flex flex-wrap md:flex-nowrap">
            <button 
              className={`flex items-center px-4 py-3 border-b-2 transition-colors duration-200 ${activeTab === 'profile' ? 'border-yellow-400 text-yellow-200' : 'border-transparent hover:text-yellow-200'}`}
              onClick={() => handleTabClick('profile')}
            >
              <User className="mr-2 h-5 w-5" />
              <span className="hidden md:inline">Admin Profile</span>
            </button>
            
            <button 
              className={`flex items-center px-4 py-3 border-b-2 transition-colors duration-200 ${activeTab === 'userList' ? 'border-yellow-400 text-yellow-200' : 'border-transparent hover:text-yellow-200'}`}
              onClick={() => handleTabClick('userList')}
            >
              <Users className="mr-2 h-5 w-5" />
              <span className="hidden md:inline">User Profiles</span>
            </button>
            
            <button 
              className={`flex items-center px-4 py-3 border-b-2 transition-colors duration-200 ${activeTab === 'update' ? 'border-yellow-400 text-yellow-200' : 'border-transparent hover:text-yellow-200'}`}
              onClick={() => handleTabClick('update')}
            >
              <Edit className="mr-2 h-5 w-5" />
              <span className="hidden md:inline">Update Profiles</span>
            </button>
            
            <button 
              className={`flex items-center px-4 py-3 border-b-2 transition-colors duration-200 ${activeTab === 'add' ? 'border-yellow-400 text-yellow-200' : 'border-transparent hover:text-yellow-200'}`}
              onClick={() => handleTabClick('add')}
            >
              <PlusCircle className="mr-2 h-5 w-5" />
              <span className="hidden md:inline">Add Profile</span>
            </button>
            
            <button 
              className="flex items-center px-4 py-3 text-white bg-red-800 rounded-md ml-2 hover:bg-red-900 transition-colors duration-200"
              onClick={() => console.log('Logging out...')}
            >
              <LogOut className="mr-2 h-5 w-5" />
              <span className="hidden md:inline">Logout</span>
            </button>
          </nav>
        </div>
      </div>
    </div>
  );
};

export default ProfileNavbar;
