import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/Profile.css";

function Profile() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    fullname: "",
    email: "",
    phone_no: "",
    gender: "",
    dob: "",
    occupation: "",
    height: "",
    marital_status: "",
    religion: "",
    education: "",
    min_age: "",
    max_age: "",
    min_height: "",
    max_height: "",
    marital_status_preferences: [],
  });

  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserProfile = async () => {
      const token = localStorage.getItem("token");

      if (!token) {
        navigate("/login");
        return;
      }

      try {
        const response = await axios.get("http://localhost:5000/api/user/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setUser(response.data);
        setFormData({
          ...response.data,
          dob: response.data.dob ? response.data.dob.split("T")[0] : "",
        });
        setLoading(false);
      } catch (err) {
        console.error("Error fetching profile:", err);
        setError("Failed to load profile. Please try again.");
        setLoading(false);

        if (err.response?.status === 401) {
          localStorage.removeItem("token");
          navigate("/login");
        }
      }
    };

    fetchUserProfile();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");

    try {
      const response = await axios.put("http://localhost:5000/api/user/profile", formData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUser(response.data);
      setIsEditing(false);
      alert("Profile updated successfully!");
    } catch (err) {
      console.error("Error updating profile:", err);
      alert("Failed to update profile. Please try again.");
    }
  };

  const toggleEdit = () => setIsEditing(!isEditing);

  return (
    <div className="profile-container">
      <div className="profile-card">
        <div className="profile-header">
          <h2>{isEditing ? "Edit Profile" : "Your Profile"}</h2>
          <div className="profile-actions">
            {!isEditing && (
              <button className="edit-profile-btn" onClick={toggleEdit}>Edit Profile</button>
            )}
            <button className="logout-btn" onClick={handleLogout}>Logout</button>
          </div>
        </div>

        {isEditing ? (
          <form className="edit-profile-form" onSubmit={handleSubmit}>
            {Object.keys(formData).map((key) => (
              key !== "email" && (
                <div className="form-group" key={key}>
                  <label>{key.replace("_", " ").toUpperCase()}</label>
                  <input
                    type="text"
                    name={key}
                    value={formData[key]}
                    onChange={handleChange}
                  />
                </div>
              )
            ))}
            <div className="form-buttons">
              <button type="submit" className="save-btn">Save Changes</button>
              <button type="button" className="cancel-btn" onClick={toggleEdit}>Cancel</button>
            </div>
          </form>
        ) : (
          <div className="profile-details">
            {Object.keys(user || {}).map((key) => (
              key !== "email" && (
                <div className="info-group" key={key}>
                  <span className="info-label">{key.replace("_", " ").toUpperCase()}</span>
                  <span className="info-value">{user[key]}</span>
                </div>
              )
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default Profile;
