import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/Profile.css';

function VerifyEmail() {
  const { token } = useParams();
  const [status, setStatus] = useState('verifying');
  const [message, setMessage] = useState('Verifying your email...');
  const navigate = useNavigate();

  useEffect(() => {
    const verifyEmail = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/auth/verify-email/${token}`);
        
        if (response.data.success) {
          setStatus('success');
          setMessage('Your email has been successfully verified!');
          
          // Store the verification response token and user
          localStorage.setItem('token', response.data.token);
          localStorage.setItem('user', JSON.stringify(response.data.user));
          
          // Auto-redirect to profile after 3 seconds
          setTimeout(() => {
            navigate('/profile');
          }, 3000);
        } else {
          setStatus('error');
          setMessage(response.data.message || 'Verification failed. Please try again.');
        }
      } catch (error) {
        setStatus('error');
        setMessage(error.response?.data?.message || 'Verification failed. The link may be invalid or expired.');
      }
    };

    if (token) {
      verifyEmail();
    } else {
      setStatus('error');
      setMessage('Invalid verification link.');
    }
  }, [token, navigate]);

  return (
    <div className="auth-container">
      <div className="auth-card">
        <div className="auth-header">
          <h2>Email Verification</h2>
        </div>
        
        <div className={`verification-message ${status}`}>
          <p>{message}</p>
          
          {status === 'success' && (
            <div>
              <p>You'll be redirected to your profile automatically in a few seconds.</p>
              <Link to="/profile" className="submit-btn">
                Go to Profile Now
              </Link>
            </div>
          )}
          
          {status === 'error' && (
            <div>
              <p>Please try registering again or contact support if the problem persists.</p>
              <div className="auth-footer">
                <Link to="/register" className="submit-btn">
                  Return to Registration
                </Link>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default VerifyEmail;
