import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/Profile.css";

function Profile() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const [showUserList, setShowUserList] = useState(false);
  const [userList, setUserList] = useState([]);
  const [statusFilter, setStatusFilter] = useState("all");
  const [formData, setFormData] = useState({
    fullname: "",
    email: "",
    phone_no: "",
    gender: "",
    dob: "",
  });
  
  const navigate = useNavigate();
  
  useEffect(() => {
    const fetchUserProfile = async () => {
      const token = localStorage.getItem("token");
      
      if (!token) {
        navigate("/login");
        return;
      }
      
      try {
        const response = await axios.get("http://localhost:5000/api/user/profile", {
          headers: { Authorization: `Bearer ${token}` }
        });
        
        setUser(response.data);
        setFormData({
          fullname: response.data.fullname || "",
          email: response.data.email || "",
          phone_no: response.data.phone_no || "",
          gender: response.data.gender || "",
          dob: response.data.dob ? response.data.dob.split('T')[0] : "",
        });
        
        // If not admin, redirect to regular profile
        if (!response.data.isAdmin) {
          navigate("/user-profile");
        }
        
        setLoading(false);
      } catch (err) {
        console.error("Error fetching profile:", err);
        setError("Failed to load profile. Please try again.");
        setLoading(false);
        
        // If token is invalid, redirect to login
        if (err.response?.status === 401) {
          localStorage.removeItem("token");
          navigate("/login");
        }
      }
    };
    
    fetchUserProfile();
  }, [navigate]);

  const fetchAllUsers = async () => {
    const token = localStorage.getItem("token");
    
    if (!token) {
      navigate("/login");
      return;
    }
    
    try {
      setLoading(true);
      let url = "http://localhost:5000/api/user/all";
      if (statusFilter !== "all") {
        url = `http://localhost:5000/api/user/all?status=${statusFilter}`;
      }
      
      const response = await axios.get(url, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      setUserList(response.data);
      setShowUserList(true);
      setLoading(false);
    } catch (err) {
      console.error("Error fetching users:", err);
      alert("Failed to load users. Please try again.");
      setLoading(false);
    }
  };

  const handleStatusChange = async (userId, newStatus) => {
    const token = localStorage.getItem("token");
    
    try {
      await axios.put(
        `http://localhost:5000/api/user/${userId}/status`,
        { status: newStatus },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      // Refresh user list after status change
      fetchAllUsers();
      alert(`User status updated to ${newStatus}`);
    } catch (err) {
      console.error("Error updating user status:", err);
      alert("Failed to update user status. Please try again.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");
    
    try {
      const response = await axios.put(
        "http://localhost:5000/api/user/profile",
        formData,
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );
      
      setUser(response.data);
      setIsEditing(false);
      // Show success message
      alert("Profile updated successfully!");
    } catch (err) {
      console.error("Error updating profile:", err);
      alert("Failed to update profile. Please try again.");
    }
  };

  const toggleEdit = () => {
    setIsEditing(!isEditing);
    setShowUserList(false);
  };

  const toggleUserList = () => {
    if (!showUserList) {
      fetchAllUsers();
    } else {
      setShowUserList(false);
    }
  };

  const handleFilterChange = (e) => {
    setStatusFilter(e.target.value);
  };

  useEffect(() => {
    if (showUserList) {
      fetchAllUsers();
    }
  }, [statusFilter]);

  if (loading) {
    return (
      <div className="profile-container">
        <div className="loading-spinner">Loading...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="profile-container">
        <div className="error-message">{error}</div>
      </div>
    );
  }

  return (
    <div className="profile-container">
      <div className="profile-card">
        <div className="profile-header">
          <h2>{isEditing ? "Edit Admin Profile" : showUserList ? "User Management" : "Admin Profile"}</h2>
          <div className="profile-actions">
            <Link to="/addUser" className="edit-profile-btn">Add User</Link>
            <button className="view-users-btn" onClick={toggleUserList}>
              {showUserList ? "Hide Users" : "Manage Users"}
            </button>
            {!isEditing && !showUserList && (
              <button className="edit-profile-btn" onClick={toggleEdit}>
                Edit Profile
              </button>
            )}
            <button className="logout-btn" onClick={handleLogout}>
              Logout
            </button>
          </div>
        </div>
         
        {user && !isEditing && !showUserList && (
          <div className="profile-details">
            <div className="profile-photo">
              <div className="default-avatar admin-avatar">
                {user.fullname ? user.fullname.charAt(0).toUpperCase() : "A"}
              </div>
            </div>
            
            <div className="profile-info">
              <div className="admin-badge">Administrator</div>
              
              <div className="info-group">
                <span className="info-label">Full Name</span>
                <span className="info-value">{user.fullname}</span>
              </div>
              
              <div className="info-group">
                <span className="info-label">Email</span>
                <span className="info-value">{user.email}</span>
              </div>
              
              <div className="info-group">
                <span className="info-label">Phone Number</span>
                <span className="info-value">{user.phone_no}</span>
              </div>
              
              <div className="info-group">
                <span className="info-label">Gender</span>
                <span className="info-value">
                  {user.gender.charAt(0).toUpperCase() + user.gender.slice(1)}
                </span>
              </div>
              
              <div className="info-group">
                <span className="info-label">Date of Birth</span>
                <span className="info-value">
                  {new Date(user.dob).toLocaleDateString()}
                </span>
              </div>
              
              <div className="dashboard-summary">
                <div className="stat-card">
                  <div className="stat-value">{userList.filter(u => u.status === "active").length}</div>
                  <div className="stat-label">Active Users</div>
                </div>
                <div className="stat-card">
                  <div className="stat-value">{userList.filter(u => u.status === "pending").length}</div>
                  <div className="stat-label">Pending Users</div>
                </div>
                <div className="stat-card">
                  <div className="stat-value">{userList.filter(u => u.status === "deactive").length}</div>
                  <div className="stat-label">Deactivated Users</div>
                </div>
              </div>
            </div>
          </div>
        )}

        {showUserList && (
          <div className="user-list-container">
            <div className="filter-controls">
              <label htmlFor="status-filter">Filter by Status:</label>
              <select 
                id="status-filter" 
                value={statusFilter} 
                onChange={handleFilterChange}
                className="status-filter"
              >
                <option value="all">All Users</option>
                <option value="active">Active</option>
                <option value="pending">Pending</option>
                <option value="deactive">Deactivated</option>
              </select>
            </div>
            
            <table className="user-table">
              <thead>
                <tr>
                  <th>Full Name</th>
                  <th>Email</th>
                  <th>Phone Number</th>
                  <th>Gender</th>
                  <th>Date of Birth</th>
                  <th>Status</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {userList.map((user, index) => (
                  <tr key={index} className={`status-${user.status}`}>
                    <td>{user.fullname}</td>
                    <td>{user.email}</td>
                    <td>{user.phone_no}</td>
                    <td>{user.gender.charAt(0).toUpperCase() + user.gender.slice(1)}</td>
                    <td>{new Date(user.dob).toLocaleDateString()}</td>
                    <td>
                      <span className={`status-badge ${user.status}`}>
                        {user.status.charAt(0).toUpperCase() + user.status.slice(1)}
                      </span>
                    </td>
                    <td className="action-buttons">
                      <select 
                        value={user.status} 
                        onChange={(e) => handleStatusChange(user._id, e.target.value)}
                        className="status-dropdown"
                      >
                        <option value="active">Activate</option>
                        <option value="pending">Set Pending</option>
                        <option value="deactive">Deactivate</option>
                      </select>
                      <Link to={`/user/${user._id}`} className="view-profile-btn">
                        View
                      </Link>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
            {userList.length === 0 && (
              <div className="no-users-message">No users found</div>
            )}
          </div>
        )}

        {isEditing && (
          <form className="edit-profile-form" onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Full Name</label>
              <input
                type="text"
                name="fullname"
                value={formData.fullname}
                onChange={handleChange}
                required
              />
            </div>
            
            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>
            
            <div className="form-group">
              <label>Phone Number</label>
              <input
                type="text"
                name="phone_no"
                value={formData.phone_no}
                onChange={handleChange}
              />
            </div>
            
            <div className="form-group">
              <label>Gender</label>
              <select
                name="gender"
                value={formData.gender}
                onChange={handleChange}
                required
              >
                <option value="">Select Gender</option>
                <option value="male">Male</option>
                <option value="female">Female</option>
                <option value="other">Other</option>
              </select>
            </div>
            
            <div className="form-group">
              <label>Date of Birth</label>
              <input
                type="date"
                name="dob"
                value={formData.dob}
                onChange={handleChange}
              />
            </div>
            <div className="form-buttons">
              <button type="submit" className="save-btn">Save Changes</button>
              <button type="button" className="cancel-btn" onClick={toggleEdit}>
                Cancel
              </button>
            </div>
          </form>
        )}
      </div>
    </div>
  );
}

export default Profile;
