import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/UserProfile.css";

function UserProfile() {
  const [user, setUser] = useState(null);
  const [preferences, setPreferences] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const [editMode, setEditMode] = useState("profile");
  const [formData, setFormData] = useState({
    fullname: "",
    email: "",
    phone_no: "",
    gender: "",
    dob: "",
    occupation: "",
    height: "",
    marital_status: "",
    religion: "",
    education: "",
    min_age: "",
    max_age: "",
    min_height: "",
    max_height: ""
  });

  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserProfile = async () => {
      const token = localStorage.getItem("token");
      
      if (!token) {
        navigate("/login");
        return;
      }
      
      try {
        // Fetch user profile
        const profileResponse = await axios.get("http://localhost:5000/api/user/profile", {
          headers: { Authorization: `Bearer ${token}` }
        });
        
        // Fetch user preferences
        const preferencesResponse = await axios.get("http://localhost:5000/api/user/preferences", {
          headers: { Authorization: `Bearer ${token}` }
        });
        
        setUser(profileResponse.data);
        setPreferences(preferencesResponse.data);
        
        // Populate form data
        setFormData({
          fullname: profileResponse.data.fullname || "",
          email: profileResponse.data.email || "",
          phone_no: profileResponse.data.phone_no || "",
          gender: profileResponse.data.gender || "",
          dob: profileResponse.data.dob ? profileResponse.data.dob.split('T')[0] : "",
          occupation: profileResponse.data.occupation || "",
          height: profileResponse.data.height || "",
          marital_status: profileResponse.data.marital_status || "",
          religion: profileResponse.data.religion || "",
          education: profileResponse.data.education || "",
          min_age: preferencesResponse.data.min_age || "",
          max_age: preferencesResponse.data.max_age || "",
          min_height: preferencesResponse.data.min_height || "",
          max_height: preferencesResponse.data.max_height || ""
        });
        
        setLoading(false);
      } catch (err) {
        console.error("Error fetching profile:", err);
        setError("Failed to load profile. Please try again.");
        setLoading(false);
        
        // If token is invalid, redirect to login
        if (err.response?.status === 401) {
          localStorage.removeItem("token");
          navigate("/login");
        }
      }
    };
    
    fetchUserProfile();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    
    if (type === "checkbox") {
      setFormData(prev => {
        const currentValues = prev[name] || [];
        const newValues = checked 
          ? [...currentValues, value]
          : currentValues.filter(item => item !== value);
        return {
          ...prev,
          [name]: newValues
        };
      });
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleProfileSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");
    
    try {
      const response = await axios.put(
        "http://localhost:5000/api/user/profile",
        formData,
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );
      
      setUser(response.data);
      setIsEditing(false);
      alert("Profile updated successfully!");
    } catch (err) {
      console.error("Error updating profile:", err);
      alert("Failed to update profile. Please try again.");
    }
  };

  const handlePreferencesSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");
    
    try {
      const response = await axios.put(
        "http://localhost:5000/api/user/preferences",
        formData,
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );
      
      setPreferences(response.data);
      setIsEditing(false);
      alert("Preferences updated successfully!");
    } catch (err) {
      console.error("Error updating preferences:", err);
      alert("Failed to update preferences. Please try again.");
    }
  };

  if (loading) {
    return (
      <div className="profile-container">
        <div className="loading-spinner">Loading...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="profile-container">
        <div className="error-message">{error}</div>
      </div>
    );
  }

  return (
    <div className="profile-container">
      <div className="profile-card">
        <div className="profile-header">
          <h2>My Profile</h2>
          <div className="profile-actions">
            <button 
              className={`tab-btn ${editMode === "profile" ? "active" : ""}`}
              onClick={() => setEditMode("profile")}
            >
              Profile Details
            </button>
            <button 
              className={`tab-btn ${editMode === "preferences" ? "active" : ""}`}
              onClick={() => setEditMode("preferences")}
            >
              Partner Preferences
            </button>
            <button className="logout-btn" onClick={handleLogout}>
              Logout
            </button>
          </div>
        </div>

        {/* Preferences Edit Section - Continued */}
        {editMode === "preferences" && isEditing && (
          <form className="edit-preferences-form" onSubmit={handlePreferencesSubmit}>
            {/* Earlier preference form rows... */}
            
            <div className="checkbox-group">
              {['single', 'divorced', 'widowed'].map(status => (
                <label key={status} className="checkbox-label">
                  <input
                    type="checkbox"
                    name="marital_status"
                    value={status}
                    checked={(formData.marital_status || preferences.marital_status).includes(status)}
                    onChange={handleChange}
                  />
                  {status.charAt(0).toUpperCase() + status.slice(1)}
                </label>
              ))}
            </div>
            
            <div className="form-group">
              <label>Preferred Religion</label>
              <div className="checkbox-group">
                {['Hindu', 'Muslim', 'Christian', 'Sikh', 'Buddhist', 'Jain', 'Other'].map(religion => (
                  <label key={religion} className="checkbox-label">
                    <input
                      type="checkbox"
                      name="religion"
                      value={religion}
                      checked={(formData.religion || preferences.religion).includes(religion)}
                      onChange={handleChange}
                    />
                    {religion}
                  </label>
                ))}
              </div>
            </div>
            
            <div className="form-group">
              <label>Preferred Education</label>
              <div className="checkbox-group">
                {['Bachelors', 'Masters', 'PhD', 'Professional Degree', 'Diploma', 'Other'].map(education => (
                  <label key={education} className="checkbox-label">
                    <input
                      type="checkbox"
                      name="education"
                      value={education}
                      checked={(formData.education || preferences.education).includes(education)}
                      onChange={handleChange}
                    />
                    {education}
                  </label>
                ))}
              </div>
            </div>
            
            <div className="form-buttons">
              <button type="submit" className="save-btn">Save Preferences</button>
              <button 
                type="button" 
                className="cancel-btn" 
                onClick={() => setIsEditing(false)}
              >
                Cancel
              </button>
            </div>
          </form>
        )}
      </div>
    </div>
  );
}

export default UserProfile;
