import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/SearchFilter.css';

const SearchFilter = () => {
  const [filters, setFilters] = useState({
    ageRange: [25, 40],
    gender: '',
    religion: '',
    motherTongue: '',
    profession: '',
    education: '',
    location: '',
    maritalStatus: '',
    height: [150, 190], // in cm
    annualIncome: [0, 1000000], // in local currency
    diet: '',
    profileCreatedBy: '',
  });

  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  const religions = ['Hindu', 'Muslim', 'Christian', 'Sikh', 'Buddhist', 'Jain', 'Jewish', 'Other'];
  const motherTongues = ['Hindi', 'English', 'Tamil', 'Telugu', 'Malayalam', 'Kannada', 'Marathi', 'Bengali', 'Other'];
  const maritalStatuses = ['Never Married', 'Divorced', 'Widowed', 'Separated'];
  const dietTypes = ['Vegetarian', 'Non-Vegetarian', 'Eggetarian', 'Vegan'];
  const createdByOptions = ['Self', 'Parent', 'Sibling', 'Friend', 'Relative'];
  const educationLevels = ['High School', 'Bachelors', 'Masters', 'Doctorate', 'Diploma'];
  const professions = ['IT Professional', 'Doctor', 'Engineer', 'Teacher', 'Business', 'Government', 'Other'];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFilters({ ...filters, [name]: value });
  };

  const handleRangeChange = (name, value) => {
    setFilters({ ...filters, [name]: value });
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.post('/api/profiles/search', { ...filters, page });
      setResults(response.data.profiles);
      setTotalPages(response.data.totalPages);
      setLoading(false);
    } catch (error) {
      console.error('Error searching profiles:', error);
      setLoading(false);
    }
  };

  const handlePageChange = (newPage) => {
    setPage(newPage);
  };

  useEffect(() => {
    if (page > 1) {
      handleSearch({ preventDefault: () => {} });
    }
  }, [page]);

  return (
    <div className="search-filter-container">
      <h2>Find Your Perfect Match</h2>
      <form onSubmit={handleSearch}>
        <div className="filter-section">
          <h3>Basic Details</h3>
          
          <div className="filter-row">
            <div className="filter-field">
              <label>Age Range</label>
              <div className="range-slider">
                <input
                  type="range"
                  min="18"
                  max="70"
                  value={filters.ageRange[0]}
                  onChange={(e) => handleRangeChange('ageRange', [parseInt(e.target.value), filters.ageRange[1]])}
                />
                <input
                  type="range"
                  min="18"
                  max="70"
                  value={filters.ageRange[1]}
                  onChange={(e) => handleRangeChange('ageRange', [filters.ageRange[0], parseInt(e.target.value)])}
                />
                <div className="range-values">
                  {filters.ageRange[0]} - {filters.ageRange[1]} years
                </div>
              </div>
            </div>
            
            <div className="filter-field">
              <label>Gender</label>
              <select name="gender" value={filters.gender} onChange={handleInputChange}>
                <option value="">Any</option>
                <option value="Male">Male</option>
                <option value="Female">Female</option>
              </select>
            </div>
          </div>

          <div className="filter-row">
            <div className="filter-field">
              <label>Religion</label>
              <select name="religion" value={filters.religion} onChange={handleInputChange}>
                <option value="">Any</option>
                {religions.map(religion => (
                  <option key={religion} value={religion}>{religion}</option>
                ))}
              </select>
            </div>
            
            <div className="filter-field">
              <label>Mother Tongue</label>
              <select name="motherTongue" value={filters.motherTongue} onChange={handleInputChange}>
                <option value="">Any</option>
                {motherTongues.map(language => (
                  <option key={language} value={language}>{language}</option>
                ))}
              </select>
            </div>
          </div>
          
          <div className="filter-row">
            <div className="filter-field">
              <label>Marital Status</label>
              <select name="maritalStatus" value={filters.maritalStatus} onChange={handleInputChange}>
                <option value="">Any</option>
                {maritalStatuses.map(status => (
                  <option key={status} value={status}>{status}</option>
                ))}
              </select>
            </div>
            
            <div className="filter-field">
              <label>Profile Created By</label>
              <select name="profileCreatedBy" value={filters.profileCreatedBy} onChange={handleInputChange}>
                <option value="">Any</option>
                {createdByOptions.map(option => (
                  <option key={option} value={option}>{option}</option>
                ))}
              </select>
            </div>
          </div>
        </div>

        <div className="filter-section">
          <h3>Education & Career</h3>
          
          <div className="filter-row">
            <div className="filter-field">
              <label>Education</label>
              <select name="education" value={filters.education} onChange={handleInputChange}>
                <option value="">Any</option>
                {educationLevels.map(level => (
                  <option key={level} value={level}>{level}</option>
                ))}
              </select>
            </div>
            
            <div className="filter-field">
              <label>Profession</label>
              <select name="profession" value={filters.profession} onChange={handleInputChange}>
                <option value="">Any</option>
                {professions.map(profession => (
                  <option key={profession} value={profession}>{profession}</option>
                ))}
              </select>
            </div>
          </div>
          
          <div className="filter-row">
            <div className="filter-field">
              <label>Annual Income Range</label>
              <div className="range-slider">
                <input
                  type="range"
                  min="0"
                  max="10000000"
                  step="100000"
                  value={filters.annualIncome[0]}
                  onChange={(e) => handleRangeChange('annualIncome', [parseInt(e.target.value), filters.annualIncome[1]])}
                />
                <input
                  type="range"
                  min="0"
                  max="10000000"
                  step="100000"
                  value={filters.annualIncome[1]}
                  onChange={(e) => handleRangeChange('annualIncome', [filters.annualIncome[0], parseInt(e.target.value)])}
                />
                <div className="range-values">
                  {filters.annualIncome[0].toLocaleString()} - {filters.annualIncome[1].toLocaleString()}
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="filter-section">
          <h3>Lifestyle & Location</h3>
          
          <div className="filter-row">
            <div className="filter-field">
              <label>Location</label>
              <input
                type="text"
                name="location"
                placeholder="City, State or Country"
                value={filters.location}
                onChange={handleInputChange}
              />
            </div>
            
            <div className="filter-field">
              <label>Diet</label>
              <select name="diet" value={filters.diet} onChange={handleInputChange}>
                <option value="">Any</option>
                {dietTypes.map(diet => (
                  <option key={diet} value={diet}>{diet}</option>
                ))}
              </select>
            </div>
          </div>
          
          <div className="filter-row">
            <div className="filter-field">
              <label>Height Range (cm)</label>
              <div className="range-slider">
                <input
                  type="range"
                  min="140"
                  max="210"
                  value={filters.height[0]}
                  onChange={(e) => handleRangeChange('height', [parseInt(e.target.value), filters.height[1]])}
                />
                <input
                  type="range"
                  min="140"
                  max="210"
                  value={filters.height[1]}
                  onChange={(e) => handleRangeChange('height', [filters.height[0], parseInt(e.target.value)])}
                />
                <div className="range-values">
                  {filters.height[0]} - {filters.height[1]} cm
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="filter-actions">
          <button type="submit" className="search-btn" disabled={loading}>
            {loading ? 'Searching...' : 'Search Matches'}
          </button>
          <button type="button" className="reset-btn" onClick={() => window.location.reload()}>
            Reset All
          </button>
        </div>
      </form>

      {loading ? (
        <div className="loading-spinner">Searching for your perfect match...</div>
      ) : (
        <div className="search-results">
          <h3>Search Results ({results.length})</h3>
          
          {results.length > 0 ? (
            <>
              <div className="profiles-grid">
                {results.map(profile => (
                  <div key={profile._id} className="profile-card">
                    <div className="profile-image">
                      <img src={profile.photos[0] || '/default-avatar.png'} alt={profile.name} />
                    </div>
                    <div className="profile-info">
                      <h4>{profile.name}, {profile.age}</h4>
                      <p>{profile.profession} | {profile.education}</p>
                      <p>{profile.location}</p>
                      <button className="view-profile-btn" onClick={() => window.location.href = `/profile/${profile._id}`}>
                        View Profile
                      </button>
                    </div>
                  </div>
                ))}
              </div>
              
              {totalPages > 1 && (
                <div className="pagination">
                  <button 
                    disabled={page === 1} 
                    onClick={() => handlePageChange(page - 1)}
                  >
                    Previous
                  </button>
                  <span>Page {page} of {totalPages}</span>
                  <button 
                    disabled={page === totalPages} 
                    onClick={() => handlePageChange(page + 1)}
                  >
                    Next
                  </button>
                </div>
              )}
            </>
          ) : (
            <div className="no-results">No matches found with your criteria. Try broadening your search.</div>
          )}
        </div>
      )}
    </div>
  );
};

export default SearchFilter;
