// src/components/Dashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Dashboard.css';

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalAppointments: 0,
    totalCustomers: 0,
    totalRevenue: 0,
    recentAppointments: [],
    serviceStats: [],
    monthlyRevenue: [],
    customerGrowth: []
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('/api/dashboard', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setStats(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to load dashboard data. Please try again later.');
        setLoading(false);
        console.error('Dashboard data fetch error:', err);
      }
    };

    fetchDashboardData();
  }, []);

  // Function to calculate bar height for simple visualizations
  const calculateBarHeight = (value, maxValue) => {
    return value ? (value / maxValue) * 100 : 0;
  };

  if (loading) return <div className="loading">Loading dashboard data...</div>;
  if (error) return <div className="error-message">{error}</div>;

  // Find max values for visualization scaling
  const maxRevenue = Math.max(...stats.monthlyRevenue.map(item => item.amount), 1);
  const maxServiceCount = Math.max(...stats.serviceStats.map(service => service.count), 1);
  const maxCustomerGrowth = Math.max(...stats.customerGrowth.map(item => item.count), 1);

  return (
    <div className="dashboard-container">
      <h1>OneStopSalon Dashboard</h1>
      
      {/* Summary Cards */}
      <div className="summary-cards">
        <div className="card">
          <h3>Total Appointments</h3>
          <p className="card-value">{stats.totalAppointments}</p>
        </div>
        <div className="card">
          <h3>Total Customers</h3>
          <p className="card-value">{stats.totalCustomers}</p>
        </div>
        <div className="card">
          <h3>Total Revenue</h3>
          <p className="card-value">${stats.totalRevenue.toFixed(2)}</p>
        </div>
      </div>

      {/* Charts Section */}
      <div className="charts-container">
        {/* Simple Monthly Revenue Chart */}
        <div className="chart-card">
          <h3>Monthly Revenue</h3>
          <div className="simple-chart">
            {stats.monthlyRevenue.map((item, index) => (
              <div className="chart-bar-container" key={index}>
                <div 
                  className="chart-bar revenue-bar" 
                  style={{ height: `${calculateBarHeight(item.amount, maxRevenue)}%` }}
                >
                  <span className="bar-value">${item.amount.toFixed(0)}</span>
                </div>
                <div className="bar-label">{item.month}</div>
              </div>
            ))}
          </div>
        </div>
        
        {/* Simple Service Popularity Chart */}
        <div className="chart-card">
          <h3>Service Popularity</h3>
          <div className="simple-table-chart">
            {stats.serviceStats.map((service, index) => (
              <div className="chart-row" key={index}>
                <div className="chart-label">{service.name}</div>
                <div className="chart-bar-wrapper">
                  <div 
                    className="horizontal-bar service-bar"
                    style={{ 
                      width: `${calculateBarHeight(service.count, maxServiceCount)}%`,
                      backgroundColor: `hsl(${index * 60}, 70%, 60%)`
                    }}
                  ></div>
                  <span className="bar-count">{service.count}</span>
                </div>
              </div>
            ))}
          </div>
        </div>
        
        {/* Simple Customer Growth Chart */}
        <div className="chart-card">
          <h3>Customer Growth</h3>
          <div className="simple-chart">
            {stats.customerGrowth.map((item, index) => (
              <div className="chart-bar-container" key={index}>
                <div 
                  className="chart-bar customer-bar" 
                  style={{ height: `${calculateBarHeight(item.count, maxCustomerGrowth)}%` }}
                >
                  <span className="bar-value">{item.count}</span>
                </div>
                <div className="bar-label">{item.month}</div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Recent Appointments Table */}
      <div className="recent-appointments">
        <h3>Recent Appointments</h3>
        <table>
          <thead>
            <tr>
              <th>Customer</th>
              <th>Service</th>
              <th>Date</th>
              <th>Time</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {stats.recentAppointments.map(appointment => (
              <tr key={appointment._id}>
                <td>{appointment.customerName}</td>
                <td>{appointment.serviceName}</td>
                <td>{new Date(appointment.date).toLocaleDateString()}</td>
                <td>{appointment.time}</td>
                <td>
                  <span className={`status-badge ${appointment.status.toLowerCase()}`}>
                    {appointment.status}
                  </span>
                </td>
              </tr>
            ))}
            {stats.recentAppointments.length === 0 && (
              <tr>
                <td colSpan="5" className="no-data">No recent appointments found</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Dashboard;
