import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import Profile from './components/Profile';
import Home from './components/Home';
import './App.css';
import AddUser from './components/AddUser';
import Search from './components/Search';
import OurStory from './components/OurStory';
import Contact from './components/Contact';
import GalleryPage from './components/GalleryPage';
import CookiePolicy from './components/CookiePolicy';
import CookieBanner from './components/CookieBanner';
import PaymentForm from './components/PayPalButton';
import { Elements } from "@stripe/react-stripe-js";
import { loadStripe } from "@stripe/stripe-js";

// Simple auth check
const PrivateRoute = ({ children }) => {
  const token = localStorage.getItem("token");
  return token ? children : <Navigate to="/login" />;
};
const [price, setPrice] = useState(20.0);
const [recipeTitle, setRecipeTitle] = useState("Delicious Recipe");
const recipeId = "12345"; 
function App() {
  return (

    <Router>
       <CookieBanner />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path='/profile' element={<Profile/>}></Route>
        <Route path='/addUser' element={<AddUser/>}/>
        <Route path='/search' element={<Search/>}/>
        <Route path='/contact' element={<Contact/>}></Route>
        <Route path='/about' element={<OurStory/>}></Route>
        <Route path='/gallery' element={<GalleryPage/>}/>
        <Route path='/cookie-policy' element={<CookiePolicy/>}/>
        <Elements stripe={stripePromise}>
            <PaymentForm />
        </Elements>
        <Route 
          path='/payment' 
          element={
            <div>
              <h2>Pay for {recipeTitle}</h2>
              <p>Price: ${price}</p>
              <PayPalButton price={price} recipeTitle={recipeTitle} recipeId={recipeId} />
            </div>
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
