import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";

import axios from "axios";
import "../styles/Login.css";

function Login() {
  const [credentials, setCredentials] = useState({
    email: "",
    password: "",
    role: "user"
  });
  const [error, setError] = useState("");

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!credentials.username || !credentials.password) {
      setError("Username and Password are required!");
      return;
    }

    console.log("Logging in with", credentials);
    setError("");
    if (credentials.role === "admin") router.push("/admin");
    else router.push("/user");
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <div className="auth-header">
          <h2>Login to Your Account</h2>
          <p>Find your perfect match with us!</p>
        </div>
        
        <form onSubmit={handleSubmit}>
          {error && <div className="error-message">{error}</div>}
          
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
            <RadioGroup value={credentials.role} onChange={(value) => setCredentials({ ...credentials, role: value })}>
              <label className="flex items-center space-x-2">
                <RadioGroupItem value="user" /> <span>User</span>
              </label>
              <label className="flex items-center space-x-2">
                <RadioGroupItem value="admin" /> <span>Admin</span>
              </label>
            </RadioGroup>
          
          <button type="submit" className="submit-btn" disabled={loading}>
            {loading ? "Logging in..." : "Login"}
          </button>
        </form>
        
        <div className="auth-footer">
          <p>
            Don't have an account? <Link to="/register">Register Now</Link>
          </p>
        </div>
      </div>
    </div>
  );
}

export default Login;
