import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './SearchFilter.css';

const SearchFilter = () => {
  const [filters, setFilters] = useState({
    looking: '',
    gender: '',
    ageRange: [18, 60],
    religion: '',
    caste: '',
    education: '',
    heightRange: {
      heightFrom: 4.0,
      heightTo: 7.0
    },
    familyStatus: '',
    familyType: '',
    familyValues: '',
    location: ''
  });

  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  
  const lookingOptions = ['bride', 'groom', 'divorce bride', 'divorce groom'];
  const genderOptions = ['male', 'female', 'other'];
  const educationOptions = ["High School", "Bachelor's", "Master's", "Doctorate", "Other"];
  const familyStatusOptions = ['Middle Class', 'Upper Middle Class', 'Rich'];
  const familyTypeOptions = ['Nuclear', 'Joint', 'Extended'];
  const familyValuesOptions = ['Traditional', 'Moderate', 'Liberal'];
  const religionOptions = ['Hindu', 'Muslim', 'Christian', 'Sikh', 'Buddhist', 'Jain', 'Other'];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFilters({ ...filters, [name]: value });
  };

  const handleHeightRangeChange = (field, value) => {
    setFilters({
      ...filters,
      heightRange: {
        ...filters.heightRange,
        [field]: parseFloat(value)
      }
    });
  };

  const handleAgeRangeChange = (index, value) => {
    const newAgeRange = [...filters.ageRange];
    newAgeRange[index] = parseInt(value);
    setFilters({ ...filters, ageRange: newAgeRange });
  };

  const handleSearch = async (e) => {
    if (e) e.preventDefault();
    setLoading(true);
    
    try {
      const response = await axios.post('/api/users/search', {
        ...filters,
        page: currentPage
      });
      
      setResults(response.data.users);
      setTotalPages(response.data.totalPages);
      setLoading(false);
    } catch (error) {
      console.error('Error searching profiles:', error);
      setLoading(false);
    }
  };

  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
  };

  useEffect(() => {
    if (currentPage > 1) {
      handleSearch();
    }
  }, [currentPage]);

  const formatHeight = (height) => {
    const feet = Math.floor(height);
    const inches = Math.round((height - feet) * 12);
    return `${feet}'${inches}"`;
  };

  const calculateAge = (dob) => {
    const birthDate = new Date(dob);
    const today = new Date();
    let age = today.getFullYear() - birthDate.getFullYear();
    const m = today.getMonth() - birthDate.getMonth();
    
    if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) {
      age--;
    }
    
    return age;
  };

  return (
    <div className="search-filter-container">
      <h2>Find Your Perfect Match</h2>
      
      <form onSubmit={handleSearch}>
        <div className="filter-section">
          <h3>Basic Details</h3>
          
          <div className="filter-row">
            <div className="filter-field">
              <label>Looking For</label>
              <select name="looking" value={filters.looking} onChange={handleInputChange}>
                <option value="">Any</option>
                {lookingOptions.map(option => (
                  <option key={option} value={option}>{option.charAt(0).toUpperCase() + option.slice(1)}</option>
                ))}
              </select>
            </div>
            
            <div className="filter-field">
              <label>Gender</label>
              <select name="gender" value={filters.gender} onChange={handleInputChange}>
                <option value="">Any</option>
                {genderOptions.map(option => (
                  <option key={option} value={option}>{option.charAt(0).toUpperCase() + option.slice(1)}</option>
                ))}
              </select>
            </div>
          </div>
          
          <div className="filter-row">
            <div className="filter-field">
              <label>Religion</label>
              <select name="religion" value={filters.religion} onChange={handleInputChange}>
                <option value="">Any</option>
                {religionOptions.map(option => (
                  <option key={option} value={option}>{option}</option>
                ))}
              </select>
            </div>
            
            <div className="filter-field">
              <label>Caste</label>
              <input
                type="text"
                name="caste"
                placeholder="Enter caste"
                value={filters.caste}
                onChange={handleInputChange}
              />
            </div>
          </div>
          
          <div className="filter-row">
            <div className="filter-field">
              <label>Age Range</label>
              <div className="range-slider">
                <div className="range-values">
                  {filters.ageRange[0]} - {filters.ageRange[1]} years
                </div>
                <input
                  type="range"
                  min="18"
                  max="70"
                  value={filters.ageRange[0]}
                  onChange={(e) => handleAgeRangeChange(0, e.target.value)}
                />
                <input
                  type="range"
                  min="18"
                  max="70"
                  value={filters.ageRange[1]}
                  onChange={(e) => handleAgeRangeChange(1, e.target.value)}
                />
              </div>
            </div>
            
            <div className="filter-field">
              <label>Education</label>
              <select name="education" value={filters.education} onChange={handleInputChange}>
                <option value="">Any</option>
                {educationOptions.map(option => (
                  <option key={option} value={option}>{option}</option>
                ))}
              </select>
            </div>
          </div>
          
          <div className="filter-row">
            <div className="filter-field">
              <label>Height Range (in feet)</label>
              <div className="height-range">
                <div className="height-field">
                  <label>From</label>
                  <select 
                    value={filters.heightRange.heightFrom}
                    onChange={(e) => handleHeightRangeChange('heightFrom', e.target.value)}
                  >
                    {Array.from({ length: 41 }, (_, i) => 3 + i * 0.1).map(height => (
                      <option key={height} value={height.toFixed(1)}>
                        {formatHeight(height)}
                      </option>
                    ))}
                  </select>
                </div>
                <div className="height-field">
                  <label>To</label>
                  <select 
                    value={filters.heightRange.heightTo}
                    onChange={(e) => handleHeightRangeChange('heightTo', e.target.value)}
                  >
                    {Array.from({ length: 41 }, (_, i) => 3 + i * 0.1).map(height => (
                      <option key={height} value={height.toFixed(1)}>
                        {formatHeight(height)}
                      </option>
                    ))}
                  </select>
                </div>
              </div>
            </div>
            
            <div className="filter-field">
              <label>Location</label>
              <input
                type="text"
                name="location"
                placeholder="City or region"
                value={filters.location}
                onChange={handleInputChange}
              />
            </div>
          </div>
        </div>
        
        <div className="filter-section">
          <h3>Family Details</h3>
          
          <div className="filter-row">
            <div className="filter-field">
              <label>Family Status</label>
              <select name="familyStatus" value={filters.familyStatus} onChange={handleInputChange}>
                <option value="">Any</option>
                {familyStatusOptions.map(option => (
                  <option key={option} value={option}>{option}</option>
                ))}
              </select>
            </div>
            
            <div className="filter-field">
              <label>Family Type</label>
              <select name="familyType" value={filters.familyType} onChange={handleInputChange}>
                <option value="">Any</option>
                {familyTypeOptions.map(option => (
                  <option key={option} value={option}>{option}</option>
                ))}
              </select>
            </div>
          </div>
          
          <div className="filter-row">
            <div className="filter-field">
              <label>Family Values</label>
              <select name="familyValues" value={filters.familyValues} onChange={handleInputChange}>
                <option value="">Any</option>
                {familyValuesOptions.map(option => (
                  <option key={option} value={option}>{option}</option>
                ))}
              </select>
            </div>
          </div>
        </div>
        
        <div className="filter-actions">
          <button type="submit" className="search-btn" disabled={loading}>
            {loading ? 'Searching...' : 'Find Matches'}
          </button>
          <button 
            type="button" 
            className="reset-btn"
            onClick={() => window.location.reload()}
          >
            Reset All
          </button>
        </div>
      </form>
      
      {loading ? (
        <div className="loading-spinner">Searching for your perfect match...</div>
      ) : (
        <div className="search-results">
          <h3>Search Results ({results.length})</h3>
          
          {results.length > 0 ? (
            <>
              <div className="profiles-grid">
                {results.map(profile => (
                  <div key={profile._id} className="profile-card">
                    <div className="profile-image">
                      <img 
                        src={profile.profilePhotoPath || '/images/default-profile.png'} 
                        alt={profile.fullname} 
                      />
                    </div>
                    <div className="profile-info">
                      <h4>{profile.fullname}, {profile.age}</h4>
                      <p>{profile.education} | {profile.religion} {profile.caste ? `(${profile.caste})` : ''}</p>
                      <p>{profile.location || 'Location not specified'}</p>
                      <p className="profile-looking">Looking for: {profile.looking}</p>
                      <button 
                        className="view-profile-btn"
                        onClick={() => window.location.href = `/profile/${profile._id}`}
                      >
                        View Profile
                      </button>
                    </div>
                  </div>
                ))}
              </div>
              
              {totalPages > 1 && (
                <div className="pagination">
                  <button 
                    disabled={currentPage === 1} 
                    onClick={() => handlePageChange(currentPage - 1)}
                  >
                    Previous
                  </button>
                  <span>Page {currentPage} of {totalPages}</span>
                  <button 
                    disabled={currentPage === totalPages} 
                    onClick={() => handlePageChange(currentPage + 1)}
                  >
                    Next
                  </button>
                </div>
              )}
            </>
          ) : (
            <div className="no-results">No matches found with your criteria. Try broadening your search.</div>
          )}
        </div>
      )}
    </div>
  );
};

export default SearchFilter;
