import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/Profile.css";

function  AdminProfile() {
  const [admin, setAdmin] = useState(null);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const [selectedUserStatus, setSelectedUserStatus] = useState("");
  const [formData, setFormData] = useState({
    fullname: "",
    email: "",
    phone_no: "",
    gender: "",
    dob: ""
  });
  
  const navigate = useNavigate();
  
  useEffect(() => {
    const fetchAdminProfile = async () => {
      const token = localStorage.getItem("token");
      
      if (!token) {
        navigate("/admin/login");
        return;
      }
      
      try {
        // Fetch admin profile
        const adminResponse = await axios.get("http://localhost:5000/api/users/admin/profile", {
          headers: { Authorization: `Bearer ${token}` }
        });
        
        setAdmin(adminResponse.data);
        setFormData({
          fullname: adminResponse.data.fullname || "",
          email: adminResponse.data.email || "",
          phone_no: adminResponse.data.phone_no || "",
          gender: adminResponse.data.gender || "",
          dob: adminResponse.data.dob ? adminResponse.data.dob.split('T')[0] : ""
        });

        // Fetch users based on selected status
        const usersResponse = await axios.get(`http://localhost:5000/api/admin/users?status=${selectedUserStatus}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        
        setUsers(usersResponse.data);
        setLoading(false);
      } catch (err) {
        console.error("Error fetching admin profile:", err);
        setError("Failed to load profile. Please try again.");
        setLoading(false);
        
        // If token is invalid, redirect to login
        if (err.response?.status === 401) {
          localStorage.removeItem("token");
          navigate("/admin/login");
        }
      }
    };
    
    fetchAdminProfile();
  }, [navigate, selectedUserStatus]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/admin/login");
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");
    
    try {
      const response = await axios.put(
        "http://localhost:5000/api/admin/profile",
        formData,
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );
      
      setAdmin(response.data);
      setIsEditing(false);
      alert("Profile updated successfully!");
    } catch (err) {
      console.error("Error updating profile:", err);
      alert("Failed to update profile. Please try again.");
    }
  };

  const handleUserStatusChange = async (userId, newStatus) => {
    const token = localStorage.getItem("token");
    
    try {
      await axios.put(
        `http://localhost:5000/api/admin/users/${userId}/status`,
        { status: newStatus },
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );
      
      // Refresh users list
      const usersResponse = await axios.get(`http://localhost:5000/api/admin/users?status=${selectedUserStatus}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      setUsers(usersResponse.data);
      alert("User status updated successfully!");
    } catch (err) {
      console.error("Error updating user status:", err);
      alert("Failed to update user status. Please try again.");
    }
  };

  const toggleEdit = () => {
    setIsEditing(!isEditing);
  };

  if (loading) {
    return (
      <div className="admin-profile-container">
        <div className="loading-spinner">Loading...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="admin-profile-container">
        <div className="error-message">{error}</div>
      </div>
    );
  }

  return (
    <div className="admin-profile-container">
      <div className="admin-profile-card">
        <div className="profile-header">
          <h2>{isEditing ? "Edit Profile" : "Admin Profile"}</h2>
          <div className="profile-actions">
            <select 
              value={selectedUserStatus} 
              onChange={(e) => setSelectedUserStatus(e.target.value)}
            >
              <option value="">All Users</option>
              <option value="active">Active</option>
              <option value="inactive">Inactive</option>
              <option value="pending">Pending</option>
            </select>
            {!isEditing && (
              <button className="edit-profile-btn" onClick={toggleEdit}>
                Edit Profile
              </button>
            )}
            <button className="logout-btn" onClick={handleLogout}>
              Logout
            </button>
          </div>
        </div>
        
        {admin && !isEditing && (
          <div className="profile-details">
            <div className="profile-photo">
              <div className="default-avatar">
                {admin.fullname ? admin.fullname.charAt(0).toUpperCase() : "A"}
              </div>
            </div>
            
            <div className="profile-info">
              <div className="info-group">
                <span className="info-label">Full Name</span>
                <span className="info-value">{admin.fullname}</span>
              </div>
              
              <div className="info-group">
                <span className="info-label">Email</span>
                <span className="info-value">{admin.email}</span>
              </div>
              
              <div className="info-group">
                <span className="info-label">Phone Number</span>
                <span className="info-value">{admin.phone_no}</span>
              </div>
            </div>
          </div>
        )}

        {isEditing && (
          <form className="edit-profile-form" onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Full Name</label>
              <input
                type="text"
                name="fullname"
                value={formData.fullname}
                onChange={handleChange}
                required
              />
            </div>
            
            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
                readOnly
              />
            </div>
            
            <div className="form-group">
              <label>Phone Number</label>
              <input
                type="text"
                name="phone_no"
                value={formData.phone_no}
                onChange={handleChange}
              />
            </div>
            
            <div className="form-buttons">
              <button type="submit" className="save-btn">Save Changes</button>
              <button type="button" className="cancel-btn" onClick={toggleEdit}>
                Cancel
              </button>
            </div>
          </form>
        )}

        {/* User Management Section */}
        <div className="user-management-section">
          <h3>User Management</h3>
          <table className="users-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user) => (
                <tr key={user._id}>
                  <td>{user.fullname}</td>
                  <td>{user.email}</td>
                  <td>{user.status}</td>
                  <td>
                    <select 
                      value={user.status}
                      onChange={(e) => handleUserStatusChange(user._id, e.target.value)}
                    >
                      <option value="active">Active</option>
                      <option value="inactive">Inactive</option>
                      <option value="pending">Pending</option>
                    </select>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default AdminProfile;
