import { useState } from "react";
import { useRouter } from "next/router";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";

export default function LoginForm() {
  const router = useRouter();
  const [credentials, setCredentials] = useState({
    username: "",
    password: "",
    role: "user"
  });
  const [error, setError] = useState("");

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!credentials.username || !credentials.password) {
      setError("Username and Password are required!");
      return;
    }

    console.log("Logging in with", credentials);
    setError("");
    if (credentials.role === "admin") router.push("/admin");
    else router.push("/user");
  };

  return (
    <div className="p-6 flex flex-col items-center">
      <h1 className="text-xl font-bold">Attendance Management System</h1>
      <Card className="mt-6 w-full max-w-md">
        <CardContent>
          <h2 className="text-lg font-semibold text-center">Login Panel</h2>
          {error && <p className="text-red-500 text-center">{error}</p>}
          <form onSubmit={handleSubmit} className="space-y-4 mt-4">
            <Input
              name="username"
              placeholder="Username"
              value={credentials.username}
              onChange={handleChange}
              required
            />
            <Input
              type="password"
              name="password"
              placeholder="Password"
              value={credentials.password}
              onChange={handleChange}
              required
            />
            <RadioGroup value={credentials.role} onChange={(value) => setCredentials({ ...credentials, role: value })}>
              <label className="flex items-center space-x-2">
                <RadioGroupItem value="user" /> <span>User</span>
              </label>
              <label className="flex items-center space-x-2">
                <RadioGroupItem value="admin" /> <span>Admin</span>
              </label>
            </RadioGroup>
            <Button type="submit" className="w-full">Login</Button>
          </form>
          <div className="mt-4 text-center">
            <a href="/reset" className="text-blue-500">Reset Password</a> | 
            <a href="/signup" className="text-blue-500"> Create New Account</a>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}
